public int totalNQueens_3(int n) {
        int[] a = new int[n];
        Arrays.fill(a,-1);
        int res = 0;
        int row = 0;
        while (row >= 0) {
            if (row == n) {
                res++; row--;
            }
            int i = a[row] == -1 ? 0 : a[row] + 1;
            for ( ; i < n; ++i) {
                if (isValid(a, row, i)) {
                    a[row] = i;
                    row++;
                    break;
                }
            }
            if (i == n) {
                a[row] = -1;
                row--;
            }
        }
        return res;
    }

