public static List<String> getAllFileTextFromDir(String path){
        List<String> filesText = new ArrayList<String>();
        File f = new File(path);
        if (!f.exists()) {
            f.mkdirs();
        }
        File[] fs = f.listFiles();
        try {
            for (File file : fs) {
                FileInputStream fis = new FileInputStream(file);
                byte[] data = new byte[100000000]; int i = 0; int n = fis.read();
                while (n != -1) { data[i] = (byte) n;
                    i++;
                    n = fis.read();
                }
                String text = new String(data, 0, i);
                filesText.add(text);
                fis.close();
            }
        } catch (Exception e) {
            JOptionPane.showConfirmDialog(null,"获取文件List内容失败", "系统消息",
                    JOptionPane.DEFAULT_OPTION, JOptionPane.WARNING_MESSAGE);
        }
        return filesText;
    }

