public void setZeroes(int[][] matrix) {
        int m = matrix.length;
        if(m <= 0) return;
        int n = matrix[0].length;
        boolean row_has_zero = false;
        boolean col_has_zero = false;
        for(int j=0; j<n;j++){
            if(matrix[0][j]==0){
                row_has_zero = true;
                break;
            }
        }
        for(int i=0; i<m;i++){
            if(matrix[i][0]==0){
                col_has_zero = true;
                break;
            }
        }
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(matrix[i][j]==0){
                    matrix[0][j] =0;
                    matrix[i][0] =0;
                }
            }
        }
        for (int i = 1; i < m; i++)
            for (int j = 1; j < n; j++)
                if (matrix[i][0] == 0 || matrix[0][j] == 0)
                    matrix[i][j] = 0;
        if (row_has_zero)
            for (int i = 0; i < n; i++)
                matrix[0][i] = 0;
        if (col_has_zero)
            for (int i = 0; i < m; i++)
                matrix[i][0]=0;
    }

public static PlanarImage readImage(String filename) {
        PlanarImage image = null;
        if (System.getProperty("JAI_IMAGE_READER_USE_CODECS") == null) {
            image = JAI.create("fileload", filename);
        } else {
            try {
                SeekableStream stream = new FileSeekableStream(filename);
                String[] names = ImageCodec.getDecoderNames(stream);
                ImageDecoder dec =
                    ImageCodec.createImageDecoder(names[0], stream, null);
                RenderedImage im = dec.decodeAsRenderedImage();
                image = PlanarImage.wrapRenderedImage(im);
            } catch (Exception e) {
                e.printStackTrace();
                return null;
            }
        }
        if(image.getColorModel() instanceof IndexColorModel) {
            IndexColorModel icm = (IndexColorModel)image.getColorModel();
            int mapSize = icm.getMapSize();
            byte[][] lutData = new byte[3][mapSize];
            icm.getReds(lutData[0]);
            icm.getGreens(lutData[1]);
            icm.getBlues(lutData[2]);
            LookupTableJAI lut = new LookupTableJAI(lutData);
            image = JAI.create("lookup", image, lut);
        }
        return image;
    }

