public double findKth(int A[], int B[], int k, int astart, int aend, int bstart, int bend) {
        int alen = aend - astart + 1;
        int blen = bend - bstart + 1;
        if (alen > blen) return findKth(B,A,k, bstart, bend, astart, aend);
        if (alen == 0) return B[bstart + k - 1];
        if (k == 1) return Math.min(A[astart],B[bstart]);
        int sa = Math.min(alen, k/2), sb = k- sa;
        if (A[astart+sa-1] == B[bstart+sb-1]) return A[astart+sa-1];
        else if (A[astart+sa-1] > B[bstart+sb-1]) return findKth(A,B,k - sb,astart,aend,bstart+sb,bend);
        else return findKth(A,B,k - sa,astart+sa,aend,bstart,bend);
    }

@Override
    public Object execute(ExecuteContext context) throws Exception {
        ProcessExecutor pe = new ProcessExecutor();
        List<String> output = new ArrayList<String>();
        String item = null;
        StringTokenizer token = null;
        SystemInfo info = new SystemInfo();
        if (pe.execute(output, CMDLINE)) {
            try {
                for (String line : output) {
                    if (line.startsWith("Cpu(s):")) {
                        boolean fragFormat = line.indexOf("%us,") > 0;
                        token = new StringTokenizer(line);
                        token.nextToken();
                        item = token.nextToken(); info.setCpuUser(Double.valueOf(
                                item.substring(0, item.indexOf('%')))
                                .doubleValue());
                        if (!fragFormat) {
                            token.nextToken();
                        }
                        item = token.nextToken(); info.setCpuSys(Double.valueOf(
                                item.substring(0, item.indexOf('%')))
                                .doubleValue());
                        if (!fragFormat) {
                            token.nextToken();
                        }
                        item = token.nextToken(); info.setCpuNice(Double.valueOf(
                                item.substring(0, item.indexOf('%')))
                                .doubleValue());
                        if (!fragFormat) {
                            token.nextToken();
                        }
                        item = token.nextToken(); info.setCpuIdle(Double.valueOf(
                                item.substring(0, item.indexOf('%')))
                                .doubleValue());
                    } else if (line.startsWith("Mem:")) {
                        token = new StringTokenizer(line);
                        token.nextToken();
                        item = token.nextToken(); info.setMemTotal(Long.valueOf(
                                item.substring(0, item.indexOf('k')))
                                .longValue());
                        token.nextToken();
                        item = token.nextToken(); info.setMemUsed(Long.valueOf(
                                item.substring(0, item.indexOf('k')))
                                .longValue());
                        token.nextToken();
                        item = token.nextToken(); info.setMemFree(Long.valueOf(
                                item.substring(0, item.indexOf('k')))
                                .longValue());
                        if (info.getMemTotal() > 0) {
                            info.setMemUsage(info.getMemUsed() * 100.0d / info.getMemTotal());
                        }
                    }
                }
            } finally {
}
        }
        return info;
    }

