private synchronized T doGet(Long timeoutMs)
            throws InterruptedException, ExecutionException, TimeoutException {
        if (mException != null) {
            throw new ExecutionException(mException);
        }
        if (mResultReceived) {
            return mResult;
        }
        if (timeoutMs == null) {
            wait(0);
        } else if (timeoutMs > 0) {
            wait(timeoutMs);
        }
        if (mException != null) {
            throw new ExecutionException(mException);
        }
        if (!mResultReceived) {
            throw new TimeoutException();
        }
        return mResult;
    }

