private static int getIndexForComparedRelationship(int comparison)
    { 
      int result=0;
        for (int i = 0; i < 3; i++)
      {
          result = result << 1;
          result = result | (comparison & 1);
          comparison = comparison >> 2;
      }
      return result;
    }

