public int candy_1(int[] ratings) {
        int N = ratings.length;
        if (N == 0) return 0;
        int[] height = new int[N];
        int res = 0;
        height[0] = 1;
        for (int i = 1; i < N; ++i) {
            height[i] = 1;
            if (ratings[i] > ratings[i - 1]) {
                height[i] = height[i - 1] + 1;
            }
        }
        for (int i = N - 2; i >= 0; --i) {
            if (ratings[i] > ratings[i + 1]) {
                height[i] = Math.max(height[i], height[i + 1] + 1);
            }
        }
        for (int i = 0; i < N; ++i) {
            res +=height[i];
        }
        return res;
    }

