private static long parseLong(String s) {
        String orig = s;
        long ret = -1;
        long scale = 1;
        try {
            if (s.indexOf("million") > -1) scale = 1000000;
            if (s.indexOf("billion") > -1) scale = 1000000000;
            s = s.replaceAll("\\$", "");
            int index = s.indexOf("%");
            if (index > -1)
                s = s.substring(0, index);
            index = s.indexOf(" ");
            if (index > -1)
                s = s.substring(0, index);
            s = s.replaceAll(",", "");
            ret = Integer.parseInt(s);
        } catch (Exception ex) {
            }
        return ret * scale;
    }

