public double findMedianSortedArrays_2(int A[], int B[]) {
        int m = A.length, n = B.length;
        int total = m + n;
        int k = total / 2;
        if ((total&1) == 1) return findKth(A,B,k+1,0,m-1,0,n-1);
        else return (findKth(A,B,k,0,m-1,0,n-1)+findKth(A,B,k+1,0,m-1,0,n-1))/2.0;
    }

