        builder.append(charsetName);
        builder.append(": ");
        for(int i = 0; i < bytes.length; i++)
        {
            if(i > 0)
                builder.append(",");
            builder.append("0x");
            builder.append(Integer.toHexString(0xff & bytes[i]).toUpperCase());
        }
        builder.append(']');
        return builder.toString();
    }

