public int minCut(String s) {
        int n = s.length();
        int[] dp = new int[n+1];
        dp[n] = -1;
        boolean[][] isP = new boolean[n][n];
        for (int i = n - 1; i >= 0; --i) {
            dp[i] = n - 1 - i;
            for (int j = i; j < n; ++j) {
                if (s.charAt(i) == s.charAt(j) && (j < i + 2 || isP[i+1][j-1])) isP[i][j] = true;
                if (isP[i][j] == true) {
                    dp[i] = Math.min(dp[i], 1 + dp[j+1]);
                }
            }
        }
        return dp[0];
    }

private void percolateDown(int i) {
        T ai = this.a[i];
        while (true) {
            int l = 2 * i;
            int r = l + 1;
            int smallest = i;
            if (r <= this.lastIndex) { if (compare(this.a[l], ai) < 0) { smallest = l;
                    if (compare(this.a[r], this.a[smallest]) < 0)
                        smallest = r;
                } else if (compare(this.a[r], ai) < 0)
                    smallest = r;
            } else {if ((l <= this.lastIndex)
                        && (compare(this.a[l], ai) < 0))
                    smallest = l;
            }
            if (i != smallest) {
                this.a[i] = this.a[smallest]; i = smallest;
            } else {
                this.a[smallest] = ai;
                return;
            }
        }
    }

