@Override
    public boolean onInterceptTouchEvent(MotionEvent event) {
        float Y = event.getY();
        float X = event.getX();
        switch (event.getActionMasked()) {
            case MotionEvent.ACTION_DOWN:
                mTouchY = Y;
                mTouchX = X;
                enableParent = false;
                break;
            case MotionEvent.ACTION_MOVE:
                Log.d("BaseViewPager","onInterceptTouchEvent action:ACTION_MOVE");
                subX = X - mTouchX;
                subY = Y - mTouchY;
                float dY = Math.abs(subY);
                float dX = Math.abs(subX);
                Log.d("onInterceptTouchEventYY",subX+"");
                switch (mGestureType) {
                    case All:
                        enableParent = false;
                        break;
                    case Vertical:
                        if (Math.abs(subY) > Math.abs(subX)) {
                            enableParent = true;
                        }
                        break;
                    case Horizon:
                        if (Math.abs(subY) < Math.abs(subX)) {
                            enableParent = true;
                        }
                        break;
                    case LeftOnly:
                        if (subX < 0) {
                            enableParent = true;
                        }
                        break;
                    case RightOnly:
                        if (subX > 0) {
                            enableParent = true;
                        }
                        break;
                    case BottomOnly:
                        if (subY < 0) {
                            enableParent = true;
                        }
                        break;
                    case TopOnly:
                        if (subY < 0) {
                            enableParent = true;
                        }
                        break;
                    default:
                        break;
                }
                break;
        }
        return super.onInterceptTouchEvent(event);
    }

