private static int threshold3(final int t, final int[] buffers, final int bufUsed) {
        if (buffers.length == 0)
            return 0;
        final int[] v = new int[t];
        v[0] = buffers[0];
        for (int k = 1; k < bufUsed; ++k) {
            final int c = buffers[k];
            final int m = Math.min(t - 1, k);
            for (int j = m; j >= 1; --j) {
                v[j] |= (c & v[j - 1]);
            }
            v[0] |= c;
        }
        return v[t - 1];
    }

