public static String getFileTextFromDirFile(String path, String fileName){
        String text = "";
        File f = new File(path);
        if (!f.exists()) {
            f.mkdirs();
        }
        f = new File(path + File.separator + fileName);
        if(!f.exists()){
            try {
                f.createNewFile();
            } catch (IOException e) {
                JOptionPane.showConfirmDialog(null,"创建文件失败，请检查是否有文件写入权限或磁盘是否有可用空间", "系统消息",
                        JOptionPane.DEFAULT_OPTION, JOptionPane.WARNING_MESSAGE);
            }
        }
        try {
            FileInputStream fis = new FileInputStream(f);
            byte[] data = new byte[(int) f.length() +50]; int i = 0; int n = fis.read();
            while (n != -1) { data[i] = (byte) n;
                i++;
                n = fis.read();
            }
            text = new String(data, 0, i);
            fis.close();
        } catch (Exception e) {
            JOptionPane.showConfirmDialog(null,"获取指定路径文件内容失败", "系统消息",
                    JOptionPane.DEFAULT_OPTION, JOptionPane.WARNING_MESSAGE);
        }
        return text;
    }

@Override
                public void messageReceived(ChannelHandlerContext ctx, MessageEvent e) throws Exception
                {
                    Object msg = e.getMessage();
                    if (msg instanceof Rpc.RpcPacket)
                    {
                        response = (Rpc.RpcPacket) msg;
                        if (response.getCounter() == responseId)
                        {
                            countDownLatch.countDown();
                        }
                        else
                        {
                            log.info("NettyRpcConnection recv a expire packet responseId={} receviedId={}", responseId, response.getCounter());
                        }
                    }
                    else
                    {
                        log.info("NettyRpcConnection unknown packet type:{} disconnect", msg.getClass().getSimpleName(), channel);
                        disconnect();
                    }
                }

