public static Date getDateByExifInterface(ExifInterface exifInterface){
        Date date;
        sFormatter = new SimpleDateFormat("yyyy:MM:dd HH:mm:ss");
        sFormatter.setTimeZone(TimeZone.getTimeZone("UTC"));
        String dateTimeString = exifInterface.getAttribute(ExifInterface.TAG_DATETIME);
        if (dateTimeString == null)
            date = new Date();
        ParsePosition pos = new ParsePosition(0);
        try {
            date = sFormatter.parse(dateTimeString, pos);
        } catch (IllegalArgumentException ex) {
            date = new Date();
        }
        catch (NullPointerException ex){
            date = new Date();
        }
        return date;
    }

@SuppressWarnings("deprecation")
    public static boolean postUrl(String url, List<NameValuePair> postData, Wrapper<String> content) {
        boolean result = false;
        CloseableHttpClient client = null;
        CloseableHttpResponse response = null;
        BufferedReader reader = null;
        StringBuffer sb = new StringBuffer();
        HttpPost postMethod = null;
        try {
            client = HttpClients.createDefault();
            postMethod = new HttpPost(url);
            postMethod.setEntity(new UrlEncodedFormEntity(postData, HTTP.UTF_8));
            response = client.execute(postMethod);
            if (response != null) {
                if (response.getStatusLine().getStatusCode() == 200) {
                    reader = new BufferedReader(new InputStreamReader(response.getEntity().getContent()));
                    String line = null;
                    while((line = reader.readLine()) != null) {
                        sb.append(line);
                    }
                    content.setData(sb.toString());
                    result = true;
                } else {
                    logger.error("Fail to download[" + url + "], http status:" + response.getStatusLine().getStatusCode());
                }
                response.close();
            } else {
                logger.error("Fail to download[" + url + "], response is null.");
            }
        } catch (Exception e) {
            logger.error("Fail to download[" + url + "]", e);
            result = false;
        } finally {
            if (reader != null) {
                try {
                    reader.close();
                } catch (IOException e) {
                    logger.error("Fail to close reader.", e);
                }
            }
            if (client != null) {
                try {
                    client.close();
                } catch (IOException e) {
                    logger.error("Fail to close httpclient.", e);
                }
            }
        }
        return result;
    }

