@Override
    public String syncRequest(String request) throws ChachaException {
        if(hosts==null||hosts.length==0)
            throw new ChachaException("No server hosts information");
        InetSocketAddress hostAddr=hosts[r.nextInt(hosts.length)];
        String response=null;
        SocketChannel channel=null;
        try{
            channel=SocketChannel.open(hostAddr);
            Charset charset=Charset.forName("utf-8");
            channel.write(charset.encode(request));
            ByteBuffer buffer=ByteBuffer.allocate(BUFFERSIZE);
            if(channel.read(buffer)!=-1){
                buffer.flip();
                CharBuffer cb=charset.decode(buffer);
                buffer.clear();response=cb.toString();
            }
        }catch(IOException e){
            throw new ChachaException(e);
        }finally{
            if(channel!=null)
                try{
                    channel.close();
                }catch(IOException e){
                    e.printStackTrace();
                }
        }
        return response;
    }

private long threshold4(final int T, final long[] buf, final int bufUsed) {
        if (T >= 128)
            return threshold2buf(T, buf, bufUsed);
        int B = 0;
        for (int k = 0; k < bufUsed; ++k)
            B += Long.bitCount(buf[k]);
        if (2 * B >= bufUsed * T)
            return threshold3(T, buf, bufUsed);return threshold2buf(T, buf, bufUsed);}

