                    chatClientMap.put(chatServer, chatClient);
                } catch (Exception e)
                {
                    log.error("创建ChatServerRpcClient失败:{}", zoneEntity, e);
                }
            }
        }
        else
        {
            log.error("ChatServerHost为null:{}", zoneEntity);
        }
    }

