public ListNode partition(ListNode head, int x) {
        ListNode dummy = new ListNode(-1);
        dummy.next = head;
        ListNode cur = dummy;
        ListNode ins = dummy;
        while (cur.next != null && cur.next.val < x) {
            cur = cur.next;
            ins = ins.next;
        }
        while (cur.next != null) {
            if (cur.next.val >= x) {
                cur = cur.next;
            } else {
                ListNode node = cur.next;
                cur.next = cur.next.next;
                node.next = ins.next;
                ins.next = node;
                ins = ins.next;
            }
        }
        return dummy.next;
    }

