public void mouseDragged(MouseEvent e)
    {
        if (prevPoint != null)
        {
            Vector3D v = panel.getViewPoint();
            double dx = e.getX() - prevPoint.x, dy = e.getY() - prevPoint.y;
            double vx = Vector3D.getX(v);
            double vy = Vector3D.getY(v);
            double vz = Vector3D.getZ(v);
            if (e.isControlDown())
            {
                vx += sensetivity*dx;
                vz += sensetivity*dy; 
            }
            else if (e.isAltDown())
            { 
             Vector3D rotationVector;
                rotationVector = Vector3D.getFromCoordinates(dx*0.01,dy*0.01,0);
                panel.rotateModel(rotationVector);
            }
            else
            {
                vx += sensetivity*dx;
                vy += sensetivity*dy;
            }
            panel.setViewPoint(Vector3D.getFromCoordinates(vx,vy,vz));
        }
        prevPoint = new Point(e.getX(),e.getY());
    }

public void reorderList_1(ListNode head) {
        ListNode slow = head, fast = head;
        if (head == null || head.next == null) return;
        while (fast.next != null && fast.next.next != null) {
            fast = fast.next.next;
            slow = slow.next;
        }
        fast = slow.next;
        slow.next = null;
        slow = head;
        ListNode pre = null;
        while (fast != null) {
            ListNode next = fast.next;
            fast.next = pre;
            pre = fast;
            fast = next;
        }
        fast = pre;
        while (fast != null) {
            ListNode fastnext = fast.next;
            fast.next = slow.next;
            slow.next = fast;
            fast = fastnext;
            slow = slow.next.next;
        }
    }

