        Node actionNode = new Node();
        actionNode.setName(Control.NS, actionName);
        actionNode.setNameSpace(Control.NS, serviceType);
        int argListCnt = argList.size();
        for (int n=0; n<argListCnt; n++) {
            Argument arg = argList.getArgument(n);
