public void reorderList_2(ListNode head) {
        ListNode slow = head, fast = head;
        if (head == null || head.next == null) return;
        while (fast.next != null && fast.next.next != null) {
            fast = fast.next.next;
            slow = slow.next;
        }
        fast = slow.next;
        while (fast.next != null) {
            ListNode move = fast.next;
            fast.next = move.next;
            move.next = slow.next;
            slow.next = move;
        }
        fast = slow.next;
        slow.next = null;
        slow = head;
        while (fast != null) {
            ListNode fastnext = fast.next;
            fast.next = slow.next;
            slow.next = fast;
            fast = fastnext;
            slow = slow.next.next;
        }
    }

