public int maximalRectangle_2(char[][] matrix) {
        if (matrix.length == 0 || matrix[0].length == 0) return 0;
        int M = matrix.length, N = matrix[0].length;
        int[] dp = new int[N+1];
        int res = 0;
        for (int i = 0; i < M; ++i) {
            for (int j = 0; j < N; ++j) {
                if (matrix[i][j] == '0') dp[j] = 0;
                else dp[j] = dp[j] + 1;
            }
            res = Math.max(res, cal(dp));
        }
        return res;
    }

