        clientsAndRequestsProcessingOperation.putIfAbsent(clientId, new UploadProcessingOperation());
        Set<UUID> set = clientToFilesMap.get(clientId);
        if (set == null) {
            set = new HashSet<UUID>();
            clientToFilesMap.put(clientId, set);
        }
        synchronized (set) {
            set.add(fileId);
        }
    }

