public String convert2url() {
        StringBuffer result = new StringBuffer();
        try {
            for (Entry<String, Object> entry : entrySet()) {
                result.append(entry.getKey()).append("=").append(URLEncoder.encode(String.valueOf(entry.getValue()), "UTF-8")).append("&");
            }
            if (result.length() > 0) {
                result.setLength(result.length() - 1);
            }
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        }
        return result.toString();
    }

public ListNode reverseBetween(ListNode head, int m, int n) {
        ListNode dummy = new ListNode(-1);
        dummy.next = head;
        ListNode first = dummy;
        for (int i = 0; i < m - 1; ++i) first = first.next;
        ListNode cur = first.next;
        for (int i = 0; i < n - m; ++i) {
            ListNode move = cur.next;
            cur.next = move.next;
            move.next = first.next;
            first.next = move;
        }
        return dummy.next;
    }

public int candy_2(int[] ratings) {
        int N = ratings.length;
        if (N == 0) return 0;
        int candy = 1, res = 1;
        int maxVal = 1, maxIdx = 0;
        for (int i = 1; i < N; ++i) {
            if (ratings[i] >= ratings[i - 1]) {
                candy = ratings[i] == ratings[i - 1] ? 1 : candy + 1;
                maxVal = candy;
                maxIdx = i;
            } else {
                if (candy == 1) {
                    if (maxVal <= i - maxIdx) {
                        ++maxVal;
                        ++res;
                    }
                    res += i - maxIdx - 1;
                }
                candy = 1;
            }
            res += candy;
        }
        return res;
    }

public boolean compressPicWithInputStream() {
        try {
            Image img = ImageIO.read(inputStream);
            if (img.getWidth(null) == -1) {
                return false;
            } else {
                int newWidth; int newHeight;
                if (this.proportion == true) {
                    double rate1 = ((double) img.getWidth(null)) / (double) outputWidth + 0.1;
                    double rate2 = ((double) img.getHeight(null)) / (double) outputHeight + 0.1;
                    double rate = rate1 > rate2 ? rate1 : rate2;
                    newWidth = (int) (((double) img.getWidth(null)) / rate);
                    newHeight = (int) (((double) img.getHeight(null)) / rate);
                } else {
                    newWidth = outputWidth; newHeight = outputHeight; }
                BufferedImage tag = new BufferedImage((int) newWidth, (int) newHeight, BufferedImage.TYPE_INT_RGB);
                tag.getGraphics().drawImage(img.getScaledInstance(newWidth, newHeight, Image.SCALE_SMOOTH), 0, 0, null);
                FileOutputStream out = new FileOutputStream(output);
                JPEGImageEncoder encoder = JPEGCodec.createJPEGEncoder(out);
