private static float parseFloat(String s) {
        String orig = s;
        float ret = -1f;
        float scale = 1f;
        try {
            if (s.indexOf("million") > -1) scale = 1000000f;
            if (s.indexOf("billion") > -1) scale = 1000000000f;
            s = s.replaceAll("\\$", "");
            int index = s.indexOf("%");
            if (index > -1)
                s = s.substring(0, index);
            index = s.indexOf(" ");
            if (index > -1)
                s = s.substring(0, index);
            s = s.replaceAll(",", "");
            ret = Float.parseFloat(s);
        } catch (Exception ex) {
            }
        return ret * scale;
    }

