public static boolean loadUrl(String url, Wrapper<String> content) {
        boolean result = false;
        CloseableHttpClient client = null;
        CloseableHttpResponse response = null;
        HttpGet getMethod = null;
        BufferedReader reader = null;
        StringBuffer sb = new StringBuffer();
        try {
            client = HttpClients.createDefault();
            getMethod = new HttpGet(url);
            response = client.execute(getMethod);
            if (response != null) {
                if (response.getStatusLine().getStatusCode() == 200) {
                    reader = new BufferedReader(new InputStreamReader(response.getEntity().getContent()));
                    String line = null;
                    while((line = reader.readLine()) != null) {
                        sb.append(line);
                    }
                    content.setData(sb.toString());
                    result = true;
                } else {
                    logger.error("Fail to download[" + url + "], http status:" + response.getStatusLine().getStatusCode());
                }
                response.close();
            } else {
                logger.error("Fail to download[" + url + "], response is null.");
            }
        } catch (Exception e) {
            logger.error("Fail to download[" + url + "]", e);
            result = false;
        } finally {
            if (reader != null) {
                try {
                    reader.close();
                } catch (IOException e) {
                    logger.error("Fail to close reader.", e);
                }
            }
            if (client != null) {
                try {
                    client.close();
                } catch (IOException e) {
                    logger.error("Fail to close httpclient.", e);
                }
            }
        }
        return result;
    }

@Override
    public String getAuthToken() throws AuthFailureError {
        final AccountManager accountManager = AccountManager.get(mContext);
        AccountManagerFuture<Bundle> future = accountManager.getAuthToken(mAccount,
                mAuthTokenType, mNotifyAuthFailure, null, null);
        Bundle result;
        try {
            result = future.getResult();
        } catch (Exception e) {
            throw new AuthFailureError("Error while retrieving auth token", e);
        }
        String authToken = null;
        if (future.isDone() && !future.isCancelled()) {
            if (result.containsKey(AccountManager.KEY_INTENT)) {
                Intent intent = result.getParcelable(AccountManager.KEY_INTENT);
                throw new AuthFailureError(intent);
            }
            authToken = result.getString(AccountManager.KEY_AUTHTOKEN);
        }
        if (authToken == null) {
            throw new AuthFailureError("Got null auth token for type: " + mAuthTokenType);
        }
        return authToken;
    }

public int singleNumber(int[] A) {
        int k = 1, n = 3;
        int[] x = new int[n];
        x[0] = ~0;
        for (int num : A) {
            int t = x[n-1];
            for (int i = n - 1; i >= 1; --i) {
                x[i] = (x[i-1] & num) | (x[i] & ~num);
            }
            x[0] = (t & num) | (x[0] & ~num);
        }
        return x[k];
    }

