public static Vector3D getAverage(Vector3D[] vectors)
  {
    if (vectors.length == 0)
       return null;
    Vector3D result = Vector3D.getFromCoordinates(0, 0, 0);
    int len = vectors.length;
     for (int i = 0; i < len; i++)
      { 
          Vector3D v = vectors[i]; 
          result.x += v.x;
          result.y += v.y;
          result.z += v.z;
      }
      double d = 1.0 / vectors.length;
        result.x *= d;
        result.y *= d;
        result.z *= d;
      return result;
  }

