public int getUpperBound(int[] A, int target) {
        int l = 0, r = A.length - 1;
        while (l <= r) {
            int mid = (l+r) / 2;
            if (A[mid] <= target) l = mid + 1;
            else r = mid - 1;
        }
        return r;
    }

