public static void saveToVRML(Model m,File f) throws IOException
  {
    FileOutputStream fout = new FileOutputStream(f);
    PrintStream out = new PrintStream(fout);
    int numTriangles = m.getNumberOfTriangles();
    Triangle []triangles = m.getTriangles();
    out.println("#VRML V2.0 utf8");
    for (int i = 0; i < numTriangles; i++)
    {
        out.println("Triangle {");
        for (int vi = 0; vi < 3; vi++)
        {
            Vector3D vertex = Triangle.getVertex(triangles[i],vi);
            out.println("   vertex" + ModelSaver.getSpacedVectorCoordinates(vertex));
        }
        out.println("}");
    }
    fout.close();
  }

public InetAddress getHostInetAddress()
    {
        String addrStr = "127.0.0.1";
        String host = getHost();
        int canmaIdx = host.lastIndexOf(":");
        if (0 <= canmaIdx) {
            addrStr = host.substring(0, canmaIdx);
            if (addrStr.charAt(0) == '[')
                addrStr = addrStr.substring(1, addrStr.length());
            if (addrStr.charAt(addrStr.length()-1) == ']')
                addrStr = addrStr.substring(0, addrStr.length()-1);
        }
        InetSocketAddress isockaddr = new InetSocketAddress(addrStr, 0);
        return isockaddr.getAddress();
    }

