private int getCode(HuffmanNode node,String decode){
        while (true){
            if (decode.charAt(0)=='0'){
                node=node.lchild;
            }
            else{
                node=node.rchild;
            }
            if (node.lchild==null&&node.rchild==null){
                return node.getValue();
            }
            if(decode.length()==1)break;
            decode=decode.substring(1);
        }
        return -1;
    }

