    if (l > l2)
        return -1;
    int f = 0;
    while (l2 - l > 1) {
        if (l % 2 == 0)
            l = l / 2;
        else
            l = l * 3 - r + 1;
        if (l2 > l) {
            return -1;
        }
        f = l * (l + l2);
        if (f > r2) {
            return f;
        }
    }
    return r2;
}

